(function () {
    'use strict';
    angular
        .module('scrollModule', [])
        .directive('ngScroll', ngScroll);

    ngScroll.$inject = ['$window'];

    function ngScroll($window) {
        return{
            restrict: 'AC',
            link: function (scope, element, attr) {
                angular.element($window).bind('scroll', function () {
                   var currentScrollY = window.scrollY;
                   var lis = document.querySelectorAll('.nav-li');
                   var cover = document.querySelector('#nav-cover');
                   var experience = document.querySelector('#nav-experience');
                   var portfolio = document.querySelector('#nav-portfolio');
                   var header = angular.element(document.querySelector('#header'));
                   var experienceY = document.querySelector('#experience').offsetTop || document.querySelector('#experience').clientTop;
                   var portfolioY = document.querySelector('#portfolio').offsetTop || document.querySelector('#portfolio').offsetTop;

                    if(currentScrollY>=experienceY && currentScrollY<portfolioY){
                       if(!(header.hasClass('fixed'))){
                           window.scrollTo(0,experienceY);
                       }
                       angular.element(document.querySelectorAll('.end-time')).addClass('fill-color');
                       header.addClass('fixed');
                       angular.element(lis).removeClass('active');
                       angular.element(experience).addClass('active');
                   }
                    else if(currentScrollY>=portfolioY){
                        if(!(header.hasClass('fixed'))){
                            header.addClass('fixed');
                            window.scrollTo(0,portfolioY);
                        }
                        if(!(angular.element(portfolio).hasClass('active'))){
                            window.scrollTo(0,portfolioY);
                        }
                        angular.element(lis).removeClass('active');
                        angular.element(portfolio).addClass('active');
                    }
                    else{
                       header.removeClass('fixed');
                       angular.element(lis).removeClass('active');
                       angular.element(cover).addClass('active');
                   }
                });
            }
        }
    }
})();

