(function () {
    'use strict';
    angular
        .module('resume', ['browserInfo', 'drawSvg','scrollModule', 'portfolioModule', 'skillModule', 'experienceModule'])
        .controller('resumeCtrl', resumeCtrl)
        .controller('portfolioCtrl',portfolioCtrl);

    resumeCtrl.$inject = ['$scope', 'supportSvg', 'skill', 'experience'];
    portfolioCtrl.$inject = ['$scope','portfolio'];

    function resumeCtrl($scope, supportSvg, skill, experience) {
        $scope.skillModel = skill;
        $scope.experience = experience;
    }

    function portfolioCtrl($scope,portfolio){
        $scope.portfolioModel = portfolio;
    }

})();




